c=======================================================================
      subroutine sint(v1,v,dv,npt,conti,conto)
c
c     interpolation for continuum 
c
      use params, only: kr
      implicit none
      real(kr) ::  conti(*), v1, v, dv, conto
      integer :: i, npt, imod

      conto=0.
      i=(v-v1)/dv+1.00001
      if(i.ge.npt)go to 10
      conto=conti(i)
      imod=mod(int(v),10)
      if(imod.gt.0) conto=(conti(i)+conti(i+1))/2.
10    continue
      return
      end
